package ArraySTudy;

public class Array111 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String Name[] = new String[5];
		Name[0]="Vaibhav";
		Name[1]="Kaveri";
		Name[2]="shrikant";
		Name[3]="ashwini";
		Name[4]="prashant";
		
		for(int i = 2 ; i<=4 ; i++)
		{
			System.out.println(Name[i]);
		}
		
		
		System.out.println(Name[3]);
		System.out.println("=========================");
		int mobile[] = new int[3];
		mobile[0]=123;
		mobile[1]=1234;
		mobile[2]=12345;
		
		
		for(int i = 0 ; i<=2 ;i++)
		{
			System.out.println(mobile[i]);
					
		}

	}
}
