package ArrrayStudy;

public class ArrayStudy111 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s[] = {"pune","nagpur","mumbai"};
		
		for(int i=0;i<=s.length-1; i++)
		{
			System.out.println(s[i]+ " ");
			
		}
		
			
		System.out.println("====================================");
		
		
		for(int i = s.length-1; i>=0 ;i--)
		{
			System.out.println(s[i]+" ");
		}
		
		
	}

}
