package ConditionalStatement.java;

public class Conditional3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		String Season = "Rainy Season";
		
		if(Season == "Winters")
		{
			System.out.println("The Atmospheric Temp is 10 to 25");
		}
		else if(Season == "Summer")
		{
			System.out.println("The Atmospheric Temp is 25 to 50");
		}
		else if(Season == "Rainy Season")
		{
			System.out.println("The Atmospheric Temp is 25 to 35");
		}
		else
		{
			System.out.println("PROBLEM");
		}
			
		
		
		
	}

}
