package SwitchCase;

public class SwitchCase2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		String month = "december";
		
		switch(month)
		{
		case "January":
		  System.out.println("The first month of the year");
		break;
		case "February":
		  System.out.println("The second month of the year");
		break;
		case "march":
		  System.out.println("The third month of the year");
		break;
		case "april":
		  System.out.println("The fourth month of the year");
		break;
		case "may":
		  System.out.println("The fifth month of the year");
		break;
		case "june":
		  System.out.println("The sixth month of the year");
		break;
		case "july":
	      System.out.println("The seventh month of the year");
		break;
		case "august":
		  System.out.println("The eighth month of the year");
		break;
		case "september":
		  System.out.println("The ninth month of the year");
		break;
		case "october":
	      System.out.println("The tenth month of the year");
		break;
		case "november":
		  System.out.println("The eleventh month of the year");
		break;
		case "december":
	      System.out.println("The twelvth month of the year");
		break;
		
		  default:
			  System.out.println("NO MONTH");
		  
		}
			
		
		
	}

}
