package SwitchCase;

public class SwitchCaseForGrade {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		char grade = 'C';
		
		switch(grade)
		{
		case 'A':
			System.out.println("Marks range  = 75-80");
		break;
		case  'B':
			System.out.println("Marks range  = 55-65");
		break;
		case 'C':
			System.out.println("Marks range = 40-50");
		break;
		case 'D':
			System.out.println("Fail");
		break;	
			
		}
		
		
	}

}
