package loopingstatement;

public class DoWhileLOop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int i = 1;
       do
       {
    	 System.out.println("ascsdacsdv "+i); 
    	 i++;
       }
		while(i<=10);
       
       System.out.println("=======================");
       
       int j = 30;
       do
       {
    	   System.out.println(j);
    	   j--;
       }
       while(j>=1);
	}
	
   
}
