package loopingstatement;

public class FOrLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
	for(int i = 1 ; i<=10 ; i++)
	{
		System.out.println(i);
	}
       		
	System.out.println("============================");

	int i = 20;
	while(i>=1)
	{
		System.out.println(i);
		i--;
	}
	System.out.println("===============================");
	
}

}