package loopingstatement;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1 ; i<=10 ; i++)
		{
			System.out.println(i);
		}
		System.out.println("======================");
		int i = 3;
		while(i<=30)
		{
			System.out.println(i);
			i=i+3;
		}
		System.out.println("=========================");
		
	
	}

}
